@push('script')
    <script type="text/javascript">
    function myFunction(name, id) {
        let selected = $('#permissions').val() || [];
        if (selected.includes(id.toString())) {
            if (!confirm('Are you sure?')) return;
            selected = selected.filter(value => value !== id.toString());
        } else {
            if (!confirm('Are you sure?')) return;
            selected.push(id.toString());
        }
        $('#permissions').val(selected).trigger('change');
    }
    </script>
@endpush